import { Component, Inject, Input, OnInit, ViewChild } from '@angular/core';
import { FormBuilder, UntypedFormControl } from '@angular/forms';
import { MatDialogRef, MAT_DIALOG_DATA } from '@angular/material/dialog';
import { MatSelect } from '@angular/material/select';
import { ReplaySubject, Subject } from 'rxjs';

@Component({
  selector: 'app-general-pop-up',
  templateUrl: './general-pop-up.component.html',
  styleUrls: ['./general-pop-up.component.scss']
})
export class GeneralPopUpComponent implements OnInit{
  @Input()
  public currentValue: string = '';
  public previousValue: string = '';

  constructor(
    public dialogRef: MatDialogRef<GeneralPopUpComponent>,
      @Inject(MAT_DIALOG_DATA) public data: any,
      public fb: FormBuilder
  ) {}




  /** control for the selected bank */
  public bankCtrl: UntypedFormControl = new UntypedFormControl();

  /** control for the MatSelect filter keyword */
  public bankFilterCtrl: UntypedFormControl = new UntypedFormControl();

  /** list of banks filtered by search keyword */
  public filteredBanks: ReplaySubject<string[]> = new ReplaySubject<string[]>(1);

  @ViewChild('singleSelect', { static: true })
  singleSelect!: MatSelect;

  /** Subject that emits when the component has been destroyed. */
  protected _onDestroy = new Subject<void>();




  ngOnInit(): void {
    this.previousValue = this.currentValue;
     // set initial selection
     this.bankCtrl.setValue(this.currentValue);

     // load the initial bank list
     this.filteredBanks.next(this.data.listOfValues.slice());

     // listen for search field value changes
     this.bankFilterCtrl.valueChanges
       .pipe()
       .subscribe(() => {
         this.filterBanks();
       });
  }

  oppoSuitsForm = this.fb.group({
    name: ['']
  });

  onNoClick(): void {
    this.dialogRef.close(this.previousValue);
  }

  onOkClick(): void {
    this.dialogRef.close(this.currentValue);
  }
  protected filterBanks() {
    if (!this.data.listOfValues) {
      return;
    }
    // get the search keyword
    let search = this.bankFilterCtrl.value;
    if (!search) {
      this.filteredBanks.next(this.data.listOfValues.slice());
      return;
    } else {
      search = search.toLowerCase();
    }
    // filter the banks
    this.filteredBanks.next(
      this.data.listOfValues.filter(((test: { name: string; }) => test.name.toLowerCase().indexOf(search) > -1)
    ));
  }
}
